// Adapter Pattern: normalize legacy interfaces

interface ModernApi {
  createResource(resource: Resource): Promise<Resource>;
  getResource(id: string): Promise<Resource | null>;
  updateResource(id: string, updates: Partial<Resource>): Promise<Resource>;
  deleteResource(id: string): Promise<void>;
}

interface LegacyApi {
  legacyCreate(data: any): string;
  legacyRead(id: string): any;
  legacyUpdate(id: string, data: any): boolean;
  legacyDelete(id: string): boolean;
}

interface Resource {
  id: string;
  name: string;
  type: string;
  createdAt: Date;
  metadata: Record<string, any>;
}

class LegacyApiAdapter implements ModernApi {
  constructor(private legacyApi: LegacyApi) {}

  async createResource(resource: Resource): Promise<Resource> {
    const legacyData = this.toLegacyFormat(resource);
    const legacyId = this.legacyApi.legacyCreate(legacyData);

    return this.fromLegacyFormat(this.legacyApi.legacyRead(legacyId));
  }

  async getResource(id: string): Promise<Resource | null> {
    const legacyData = this.legacyApi.legacyRead(id);
    if (!legacyData) return null;
    return this.fromLegacyFormat(legacyData);
  }

  async updateResource(id: string, updates: Partial<Resource>): Promise<Resource> {
    const current = await this.getResource(id);
    if (!current) throw new Error("Resource not found");

    const merged = { ...current, ...updates };
    const legacyData = this.toLegacyFormat(merged);
    this.legacyApi.legacyUpdate(id, legacyData);

    return this.fromLegacyFormat(this.legacyApi.legacyRead(id));
  }

  async deleteResource(id: string): Promise<void> {
    const success = this.legacyApi.legacyDelete(id);
    if (!success) throw new Error("Failed to delete resource");
  }

  private toLegacyFormat(resource: Resource): any {
    return {
      legacy_name: resource.name,
      legacy_type: resource.type,
      legacy_meta: JSON.stringify(resource.metadata)
    };
  }

  private fromLegacyFormat(legacyData: any): Resource {
    return {
      id: legacyData.legacy_id,
      name: legacyData.legacy_name,
      type: legacyData.legacy_type,
      createdAt: new Date(legacyData.legacy_created_at),
      metadata: JSON.parse(legacyData.legacy_meta || "{}")
    };
  }
}
