// Dual token: stable (UST) + volatile (LUNA)
function expandSupply(uint256 amount) external {
    if (price > 1.01e18) { // Above peg
        stableToken.mint(msg.sender, amount);
        volatileToken.burn(msg.sender, getVolatileAmount(amount));
    }
}

function contractSupply(uint256 amount) external {
    if (price < 0.99e18) { // Below peg
        stableToken.burn(msg.sender, amount);
        volatileToken.mint(msg.sender, getVolatileAmount(amount));
    }
}
