// Arweave transaction ID stored on-chain
contract PermanentNFT {
    mapping(uint256 => string) public arweaveIds;

    function mint(address to, string memory arweaveTxId) external {
        uint256 tokenId = _nextTokenId++;
        _mint(to, tokenId);
        arweaveIds[tokenId] = arweaveTxId; // e.g., "ar://abc123..."
    }
}
