// Auto-Compound: harvest + reinvest
function harvest() external {
    uint256 rewards = rewardToken.balanceOf(address(this));
    rewardToken.approve(dex, rewards);
    uint256 newAssets = dex.swap(rewardToken, asset, rewards);
    _deposit(newAssets); // Reinvest
    emit Harvested(rewards, newAssets);
}
