// CDP: lock collateral, mint DAI
function openVault(uint256 ethAmount) external payable {
    require(msg.value == ethAmount);
    vaults[msg.sender].collateral += ethAmount;
}

function mintDAI(uint256 amount) external {
    uint256 maxDebt = vaults[msg.sender].collateral * RATIO / 100;
    require(vaults[msg.sender].debt + amount <= maxDebt);
    dai.mint(msg.sender, amount);
}
