contract ComposableNFT is ERC998 {
    // Parent NFT owns child tokens
    mapping(uint256 => address[]) public childContracts;
    mapping(uint256 => mapping(address => uint256[])) public childTokens;

    function safeTransferChild(
        uint256 fromTokenId,
        address to,
        address childContract,
        uint256 childTokenId
    ) external {
        require(ownerOf(fromTokenId) == msg.sender);
        IERC721(childContract).safeTransferFrom(address(this), to, childTokenId);
        _removeChild(fromTokenId, childContract, childTokenId);
    }
}
