// Event delegation for list items
class TodoList {
  private list: HTMLElement;

  constructor(container: HTMLElement) {
    this.list = container;
    this.list.addEventListener('click', this.handleClick.bind(this));
  }

  private handleClick(event: Event) {
    const target = event.target as HTMLElement;

    // Check if clicked element is a todo item
    if (target.classList.contains('todo-item')) {
      this.toggleTodo(target.dataset.id!);
      return;
    }

    // Check if clicked element is a delete button
    if (target.classList.contains('delete-btn')) {
      event.stopPropagation(); // Prevent triggering item click
      this.deleteTodo(target.dataset.id!);
      return;
    }

    // Check if clicked element is inside a todo item
    const todoItem = target.closest('.todo-item') as HTMLElement;
    if (todoItem) {
      this.toggleTodo(todoItem.dataset.id!);
    }
  }

  private toggleTodo(id: string) {
    console.log(`Toggling todo ${id}`);
  }

  private deleteTodo(id: string) {
    console.log(`Deleting todo ${id}`);
  }
}

// Usage
const todoList = new TodoList(document.getElementById('todo-list')!);

