// Advanced delegation with data attributes
function createDelegatedMenu(container: HTMLElement) {
  container.addEventListener('click', (event) => {
    const target = event.target as HTMLElement;
    const action = target.dataset.action;

    if (!action) return;

    const menuItem = target.closest('[data-menu-item]') as HTMLElement;
    const itemId = menuItem?.dataset.id;

    switch (action) {
      case 'edit':
        editItem(itemId);
        break;
      case 'delete':
        deleteItem(itemId);
        break;
      case 'duplicate':
        duplicateItem(itemId);
        break;
    }
  });
}

function editItem(id: string | undefined) { console.log('Edit', id); }
function deleteItem(id: string | undefined) { console.log('Delete', id); }
function duplicateItem(id: string | undefined) { console.log('Duplicate', id); }
