// Gnosis Safe is pre-deployed, but custom module example:
contract TreasuryModule {
    GnosisSafe public safe;

    function executeFromModule(
        address to,
        uint256 value,
        bytes memory data
    ) external onlyAuthorized returns (bool) {
        return safe.execTransactionFromModule(
            to, value, data, Enum.Operation.Call
        );
    }
}
