// Green NFT with built-in carbon offset
contract GreenNFT is ERC721 {
    uint256 public constant OFFSET_PER_MINT = 0.01 ether; // KlimaDAO tokens
    IKlimaRetire public klimaRetire;

    function mint(address to, uint256 tokenId) external payable {
        require(msg.value >= MINT_PRICE + OFFSET_PER_MINT);

        // Mint NFT
        _mint(to, tokenId);

        // Retire carbon credits automatically
        klimaRetire.retireCarbon{value: OFFSET_PER_MINT}(
            beneficiary: to,
            amount: calculateFootprint(tokenId) // Based on estimated NFT footprint
        );

        emit CarbonNeutralMint(tokenId, to);
    }
}
