// Fractional reserve model (e.g., 85% USDC, 15% FXS)
function mint(uint256 fraxAmount) external {
    uint256 collateralRatio = getCollateralRatio(); // e.g., 85%
    uint256 collateralNeeded = (fraxAmount * collateralRatio) / 1e6;
    uint256 fxsToBurn = (fraxAmount * (1e6 - collateralRatio)) / 1e6;

    usdc.transferFrom(msg.sender, address(this), collateralNeeded);
    fxs.burn(msg.sender, fxsToBurn);
    frax.mint(msg.sender, fraxAmount);
}
