// Interest Rate: kink model
function getBorrowRate(uint256 utilization) public view returns (uint256) {
    if (utilization <= KINK) {
        return BASE_RATE + utilization * SLOPE1 / 1e18;
    } else {
        return BASE_RATE + KINK * SLOPE1 / 1e18 +
               (utilization - KINK) * SLOPE2 / 1e18;
    }
}
