// IPFS hash stored on-chain
contract NFTWithIPFS {
    mapping(uint256 => string) public tokenURIs; // ipfs://Qm...

    function mint(address to, string memory ipfsHash) external {
        uint256 tokenId = _nextTokenId++;
        _mint(to, tokenId);
        tokenURIs[tokenId] = string(abi.encodePacked("ipfs://", ipfsHash));
    }
}
