// LayerZero omnichain contract
import "@layerzerolabs/solidity-examples/contracts/lzApp/NonblockingLzApp.sol";

contract OmniCounter is NonblockingLzApp {
    function incrementCounter(uint16 _dstChainId) external payable {
        _lzSend(_dstChainId, abi.encode(INCREMENT_TYPE), payable(msg.sender), address(0), bytes(""), msg.value);
    }

    function _nonblockingLzReceive(uint16, bytes memory, uint64, bytes memory _payload) internal override {
        counter++;
    }
}
