// Liquidation: discount for liquidators
function liquidate(address borrower, address collateral) external {
    require(isLiquidatable(borrower));
    uint256 debt = getUserDebt(borrower);
    uint256 collateralSeized = debt * LIQUIDATION_BONUS / 100;
    _transfer(collateral, msg.sender, collateralSeized);
}
