// Micro Frontend Shell Application
import React, { Suspense } from 'react';

const ProductApp = React.lazy(() => import('product-app/App'));
const UserApp = React.lazy(() => import('user-app/App'));
const CartApp = React.lazy(() => import('cart-app/App'));

interface MicroFrontendProps {
  name: string;
  host: string;
  document?: Document;
  window?: Window;
}

function MicroFrontend({ name, host, document, window }: MicroFrontendProps) {
  return (
    <div id={name}>
      <Suspense fallback={<div>Loading {name}...</div>}>
        {/* Dynamic import and rendering of micro frontend */}
      </Suspense>
    </div>
  );
}

function App() {
  return (
    <div className="app-shell">
      <header>Company Header</header>
      <nav>Navigation</nav>
      <main>
        <MicroFrontend name="products" host="http://localhost:3001" />
        <MicroFrontend name="users" host="http://localhost:3002" />
        <MicroFrontend name="cart" host="http://localhost:3003" />
      </main>
      <footer>Company Footer</footer>
    </div>
  );
}

// Module Federation (Webpack 5)
// webpack.config.js for shell
module.exports = {
  plugins: [
    new ModuleFederationPlugin({
      name: 'shell',
      remotes: {
        'product-app': 'productApp@http://localhost:3001/remoteEntry.js',
        'user-app': 'userApp@http://localhost:3002/remoteEntry.js',
        'cart-app': 'cartApp@http://localhost:3003/remoteEntry.js',
      },
    }),
  ],
};
