mapping(uint256 => mapping(address => bool)) approvals;
uint256 public required = 3;

function approve(uint256 txId) public onlySigner {
    approvals[txId][msg.sender] = true;
}

function execute(uint256 txId) public {
    require(getApprovalCount(txId) >= required);
