contract CoveredCallVault {
    function rollover() external onlyKeeper {
        // 1. Settle previous week's options
        settleOptions();

        // 2. Select strike price (e.g., 10% OTM)
        uint256 strike = (spotPrice * 110) / 100;

        // 3. Sell covered calls for next week
        uint256 premium = optionsProtocol.sellCall(
            totalAssets(), 
            strike, 
            block.timestamp + 7 days
        );

        // 4. Distribute premium to vault
        premiumEarned += premium;
    }
}
