// Plasma exit mechanism
function startExit(uint256 utxoPos, bytes memory txBytes, bytes memory proof) external {
    require(verifyInclusion(utxoPos, txBytes, proof));
    exits[utxoPos] = Exit({
        owner: msg.sender,
        amount: getAmount(txBytes),
        exitableAt: block.timestamp + CHALLENGE_PERIOD
    });
}
