// Rage Quit: burn shares for assets
function ragequit(uint256 sharesToBurn) external {
    require(block.timestamp <= proposal.gracePeriodEnd);
    uint256 proportion = sharesToBurn * 1e18 / totalShares;

    for (uint256 i = 0; i < tokens.length; i++) {
        uint256 amount = balance[tokens[i]] * proportion / 1e18;
        tokens[i].transfer(msg.sender, amount);
    }
    _burn(msg.sender, sharesToBurn);
}
