// Delayed Reveal: placeholder â†’ real metadata
contract RevealableNFT is ERC721 {
    bool public revealed;
    string public placeholderURI;
    string public revealedBaseURI;

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        if (!revealed) return placeholderURI;
        return string(abi.encodePacked(revealedBaseURI, tokenId.toString()));
    }

    function reveal(string memory uri) external onlyOwner {
        revealedBaseURI = uri;
        revealed = true;
    }
}
