contract SemiFungible is ERC1155 {
    mapping(uint256 => uint256) public supply;

    function mint(uint256 id, uint256 amount) external {
        require(supply[id] + amount <= MAX_SUPPLY[id]);
        supply[id] += amount;
        _mint(msg.sender, id, amount, "");
    }

    // Transition: when supply reaches max, becomes unique NFT
    function isFungible(uint256 id) public view returns (bool) {
        return supply[id] < MAX_SUPPLY[id];
    }
}
