// Checkpoint submission to mainnet
function submitCheckpoint(bytes32 rootHash, uint256 startBlock, uint256 endBlock) external onlyValidator {
    require(validatorSet[msg.sender]);
    checkpoints[checkpointId] = Checkpoint({
        root: rootHash,
        start: startBlock,
        end: endBlock,
        timestamp: block.timestamp
    });
}
