// OVER-COMPLEX: Multiple layers of abstraction
type RequestHandler struct {
    validator Validator
    processor RequestProcessor
    responder ResponseBuilder
}

func (h *RequestHandler) Handle(w http.ResponseWriter, r *http.Request) {
    if err := h.validator.Validate(r); err != nil {
        h.responder.Error(w, err)
        return
    }

    result, err := h.processor.Process(r)
    if err != nil {
        h.responder.Error(w, err)
        return
    }

    h.responder.Success(w, result)
}

// SIMPLE: Direct handler
function handleUser(w http.ResponseWriter, r *http.Request) {
    var user User
    if err := json.NewDecoder(r.Body).Decode(&user); err != nil {
        http.Error(w, "Invalid JSON", http.StatusBadRequest)
        return
    }

    if user.Name == "" || user.Email == "" {
        http.Error(w, "Name and email required", http.StatusBadRequest)
        return
    }

    // Save user to database
    userID, err := saveUser(user)
    if err != nil {
        http.Error(w, "Failed to save user", http.StatusInternalServerError)
        return
    }

    w.Header().Set("Content-Type", "application/json")
    json.NewEncoder(w).Encode(map[string]interface{}{
        "success": true,
        "user_id": userID,
    })
}

