// SECOND: Phone validation
public class PhoneValidator
{
    public ValidationResult Validate(string phone)
    {
        if (string.IsNullOrEmpty(phone))
            return ValidationResult.Invalid("Phone is required");

        if (!Regex.IsMatch(phone, @"^\+?\d{10,}$"))
            return ValidationResult.Invalid("Invalid phone format");

        return ValidationResult.Valid();
    }
}

// THIRD: Password validation - Now extract common pattern
public abstract class StringValidator
{
    protected abstract string FieldName { get; }
    protected abstract bool IsValidFormat(string value);

    public ValidationResult Validate(string value)
    {
        if (string.IsNullOrEmpty(value))
            return ValidationResult.Invalid($"{FieldName} is required");

        if (!IsValidFormat(value))
            return ValidationResult.Invalid($"Invalid {FieldName.ToLower()} format");

        return ValidationResult.Valid();
    }
}

public class EmailValidator : StringValidator
{
    protected override string FieldName => "Email";
    protected override bool IsValidFormat(string value) => value.Contains("@");
}

public class PhoneValidator : StringValidator
{
    protected override string FieldName => "Phone";
    protected override bool IsValidFormat(string value) => Regex.IsMatch(value, @"^\+?\d{10,}$");
}

