        self._transformers.append(transformer_func)

    def process(self, file_path):
        """Simple public interface"""
        # Encapsulated complexity
        raw_data = self._read_file(file_path)
        validated_data = self._apply_validations(raw_data)
        transformed_data = self._apply_transformations(validated_data)
        return self._format_output(transformed_data)

    # Private methods encapsulate implementation
    def _read_file(self, file_path):
        with open(file_path, 'r', encoding=self._encoding) as f:
            return list(csv.DictReader(f, delimiter=self._delimiter))

    def _apply_validations(self, data):
        for item in data:
            for validator in self._validators:
                validator(item)
        return data

    def _apply_transformations(self, data):
        for item in data:
            for transformer in self._transformers:
                transformer(item)
        return data

    def _format_output(self, data):
        return {'processed_items': len(data), 'data': data}

