import pytest
from hypothesis import given, strategies as st

class ShoppingCart:
    def __init__(self):
        self.items = []

    def add_item(self, name, price, quantity=1):
        if price < 0:
            raise ValueError("Price cannot be negative")
        if quantity < 1:
            raise ValueError("Quantity must be positive")

        self.items.append({
            'name': name,
            'price': price,
            'quantity': quantity
        })

    def get_total(self):
        return sum(item['price'] * item['quantity'] for item in self.items)

# Unit tests
class TestShoppingCart:
    def setup_method(self):
        self.cart = ShoppingCart()

    def test_add_valid_item(self):
        self.cart.add_item('Apple', 1.50, 3)
        assert len(self.cart.items) == 1
        assert self.cart.get_total() == 4.50

    def test_add_item_negative_price_raises_error(self):
        with pytest.raises(ValueError, match="Price cannot be negative"):
            self.cart.add_item('Apple', -1.50)

    def test_add_item_zero_quantity_raises_error(self):
        with pytest.raises(ValueError, match="Quantity must be positive"):
            self.cart.add_item('Apple', 1.50, 0)

    def test_empty_cart_total_is_zero(self):
        assert self.cart.get_total() == 0

    def test_multiple_items_total(self):
        self.cart.add_item('Apple', 1.50, 2)
        self.cart.add_item('Banana', 0.75, 4)
        assert self.cart.get_total() == 4.50

# Property-based tests (hypothesis)
class TestShoppingCartProperties:
    @given(
        st.lists(
            st.tuples(
                st.text(min_size=1),
                st.floats(min_value=0, max_value=1000),
                st.integers(min_value=1, max_value=100)
            ),
            min_size=0,
            max_size=10
        )
    )
    def test_total_is_always_non_negative(self, items):
        cart = ShoppingCart()
        for name, price, quantity in items:
            cart.add_item(name, price, quantity)

        assert cart.get_total() >= 0

    @given(
        st.text(min_size=1),
        st.floats(min_value=0, max_value=1000),
        st.integers(min_value=1, max_value=100),
        st.integers(min_value=1, max_value=100)
    )
    def test_quantity_multiplication(self, name, price, quantity1, quantity2):
        cart = ShoppingCart()
        cart.add_item(name, price, quantity1)
        cart.add_item(name, price, quantity2)

        expected_total = price * (quantity1 + quantity2)
        assert abs(cart.get_total() - expected_total) < 0.01

