// PERFORMANCE-AWARE: Efficient data processing
class DataProcessor {
  private cache = new Map<string, any>();

  // Cache expensive operations
  async getProcessedData(key: string): Promise<any> {
    if (this.cache.has(key)) {
      return this.cache.get(key);
    }

    const data = await this.expensiveOperation(key);
    this.cache.set(key, data);
    return data;
  }

  // Use efficient algorithms
  findDuplicates(items: number[]): number[] {
    // O(n) time with Set vs O(n) with nested loops
    const seen = new Set<number>();
    const duplicates: number[] = [];

    for (const item of items) {
      if (seen.has(item) && !duplicates.includes(item)) {
        duplicates.push(item);
      }
      seen.add(item);
    }

    return duplicates;
  }

  // Batch operations to reduce overhead
  async processBatch(items: any[]): Promise<any[]> {
    const results: any[] = [];
    const batchSize = 100;

    for (let i = 0; i < items.length; i += batchSize) {
      const batch = items.slice(i, i + batchSize);
      const batchResults = await this.processBatchItems(batch);
      results.push(...batchResults);
    }

    return results;
  }

  // Avoid unnecessary work
  shouldProcessItem(item: any): boolean {
    // Early returns prevent unnecessary processing
    if (!item.isActive) return false;
    if (item.processedAt && Date.now() - item.processedAt < 3600000) {
      return false; // Skip if processed within last hour
    }
    return true;
  }

  private async expensiveOperation(key: string): Promise<any> {
    // Simulate expensive operation
    await new Promise(resolve => setTimeout(resolve, 100));
    return { key, data: `processed-${key}` };
  }

  private async processBatchItems(batch: any[]): Promise<any[]> {
    // Process multiple items efficiently
    return Promise.all(batch.map(item => this.expensiveOperation(item.key)));
  }
}

