// PERFORMANCE-AWARE: Efficient Java patterns
import java.util.*;
import java.util.concurrent.*;
import java.util.stream.Collectors;

public class PerformanceAwareService {
    private final ConcurrentHashMap<String, CachedResult> cache = new ConcurrentHashMap<>();
    private final ScheduledExecutorService cacheCleaner = Executors.newScheduledThreadPool(1);

    public PerformanceAwareService() {
        // Clean expired cache entries every 5 minutes
        cacheCleaner.scheduleAtFixedRate(this::cleanExpiredCache, 5, 5, TimeUnit.MINUTES);
    }

    // Efficient caching with TTL
    public Optional<String> getCachedData(String key) {
        CachedResult result = cache.get(key);
        if (result != null && !result.isExpired()) {
            return Optional.of(result.getData());
        }
        return Optional.empty();
    }

    public void cacheData(String key, String data, long ttlMillis) {
        cache.put(key, new CachedResult(data, ttlMillis));
    }

    // Efficient string processing
    public List<String> findDuplicatesEfficient(List<String> items) {
        // O(n) with HashSet vs O(n) with nested loops
        Set<String> seen = ConcurrentHashMap.newKeySet();
        Set<String> duplicates = ConcurrentHashMap.newKeySet();

        return items.parallelStream()
            .filter(item -> !seen.add(item) && duplicates.add(item))
            .distinct()
            .collect(Collectors.toList());
    }

    // Batch processing to reduce I/O overhead
    public List<ProcessedItem> processBatch(List<RawItem> items) {
        final int batchSize = 100;
        List<CompletableFuture<List<ProcessedItem>>> futures = new ArrayList<>();

        for (int i = 0; i < items.size(); i += batchSize) {
            int endIndex = Math.min(i + batchSize, items.size());
            List<RawItem> batch = items.subList(i, endIndex);

            CompletableFuture<List<ProcessedItem>> future = 
                CompletableFuture.supplyAsync(() -> processBatchItems(batch));
            futures.add(future);
        }

        return futures.stream()
            .map(CompletableFuture::join)
            .flatMap(List::stream)
            .collect(Collectors.toList());
    }

    // Memory-efficient large file processing
    public double calculateAverageFromLargeFile(String filePath) {
        // Process line by line instead of loading entire file
        double sum = 0.0;
        long count = 0;

        try (Scanner scanner = new Scanner(new File(filePath))) {
            while (scanner.hasNextLine()) {
                try {
                    double value = Double.parseDouble(scanner.nextLine().trim());
                    sum += value;
                    count++;
                } catch (NumberFormatException e) {
                    // Skip invalid lines
                }
            }
        }

        return count > 0 ? sum / count : 0.0;
    }

    private void cleanExpiredCache() {
        cache.entrySet().removeIf(entry -> entry.getValue().isExpired());
    }

    private List<ProcessedItem> processBatchItems(List<RawItem> batch) {
        // Simulate batch processing
        return batch.stream()
            .map(item -> new ProcessedItem(item.getId(), item.getData().toUpperCase()))
            .collect(Collectors.toList());
    }

    private static class CachedResult {
        private final String data;
        private final long expiryTime;

        CachedResult(String data, long ttlMillis) {
            this.data = data;
            this.expiryTime = System.currentTimeMillis() + ttlMillis;
        }

        boolean isExpired() {
            return System.currentTimeMillis() > expiryTime;
        }

        String getData() { return data; }
    }
}

