using System.Collections.Concurrent;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

public class PerformanceAwareService
{
    private readonly ConcurrentDictionary<string, CachedItem> _cache = new();
    private readonly TimeProvider _timeProvider;

    public PerformanceAwareService(TimeProvider timeProvider)
    {
        _timeProvider = timeProvider;
        // Start background cache cleanup
        Task.Run(CleanExpiredCacheAsync);
    }

    // Efficient caching with TTL
    public string? GetCachedData(string key)
    {
        if (_cache.TryGetValue(key, out var item) && !item.IsExpired(_timeProvider))
        {
            return item.Data;
        }
        return null;
    }

    public void CacheData(string key, string data, TimeSpan ttl)
    {
        _cache[key] = new CachedItem(data, _timeProvider.GetUtcNow().Add(ttl));
    }

    // Efficient duplicate finding
    public IEnumerable<T> FindDuplicates<T>(IEnumerable<T> items)
    {
        // O(n) with HashSet vs O(n) with nested loops
        var seen = new HashSet<T>();
        var duplicates = new HashSet<T>();

        foreach (var item in items)
        {
            if (!seen.Add(item))
            {
                duplicates.Add(item);
            }
        }

        return duplicates;
    }

    // Batch processing with parallelism
    public async Task<IEnumerable<ProcessedItem>> ProcessBatchAsync(
        IEnumerable<RawItem> items, 
        int batchSize = 100)
    {
        var batches = items
            .Select((item, index) => (item, index))
            .GroupBy(x => x.index / batchSize, x => x.item);

        var tasks = batches.Select(batch => 
            Task.Run(() => ProcessBatchItems(batch)));

        var results = await Task.WhenAll(tasks);
        return results.SelectMany(x => x);
    }

    // Memory-efficient large file processing
    public async Task<double> CalculateAverageFromLargeFileAsync(string filePath)
    {
        double sum = 0.0;
        long count = 0;

        await foreach (var line in File.ReadLinesAsync(filePath))
        {
            if (double.TryParse(line.Trim(), out var value))
            {
                sum += value;
                count++;
            }
        }

        return count > 0 ? sum / count : 0.0;
    }

    // Efficient grouping with LINQ
    public ILookup<string, Item> GroupItemsEfficiently(IEnumerable<Item> items)
    {
        // LINQ ToLookup is optimized for grouping
        return items.ToLookup(item => item.Category);
    }

    // Sliding window maximum - efficient algorithm
    public IEnumerable<int> SlidingWindowMaximum(int[] nums, int k)
    {
        if (nums.Length == 0 || k == 0) yield break;

        var window = new LinkedList<int>();

        for (int i = 0; i < nums.Length; i++)
        {
            // Remove elements outside current window
            while (window.Count > 0 && window.First.Value <= i - k)
                window.RemoveFirst();

            // Remove smaller elements from back
            while (window.Count > 0 && nums[window.Last.Value] <= nums[i])
                window.RemoveLast();

            window.AddLast(i);

            // Add max for current window
            if (i >= k - 1)
                yield return nums[window.First.Value];
        }
    }

    private async Task CleanExpiredCacheAsync()
    {
        while (true)
        {
            await Task.Delay(TimeSpan.FromMinutes(5));
            var expiredKeys = _cache
                .Where(kvp => kvp.Value.IsExpired(_timeProvider))
                .Select(kvp => kvp.Key)
                .ToList();

            foreach (var key in expiredKeys)
            {
                _cache.TryRemove(key, out _);
            }
        }
    }

    private IEnumerable<ProcessedItem> ProcessBatchItems(IEnumerable<RawItem> batch)
    {
        return batch.Select(item => 
            new ProcessedItem(item.Id, item.Data.ToUpper()));
    }

    private record CachedItem(string Data, DateTimeOffset ExpiryTime)
    {
        public bool IsExpired(TimeProvider timeProvider) => 
            timeProvider.GetUtcNow() > ExpiryTime;
    }
}

