// Soulbound: non-transferable
contract SoulboundToken is ERC721 {
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override {
        require(from == address(0) || to == address(0), 
            "Soulbound: transfer not allowed");
        super._beforeTokenTransfer(from, to, tokenId);
    }
}
