// State channel with dispute period
function openChannel(address participant) external payable {
    channels[channelId] = Channel({
        participants: [msg.sender, participant],
        balance: msg.value,
        nonce: 0
    });
}

function closeChannel(uint256 nonce, bytes memory signature) external {
    require(nonce > channels[channelId].nonce);
    require(verifySignature(signature));
    // Settle final state
}
