async function speedUpTransaction(originalTx: Transaction) {
  const newGasPrice = originalTx.gasPrice * 1.2; // 20% increase

  const replacementTx = {
    ...originalTx,
    nonce: originalTx.nonce, // SAME nonce
    gasPrice: newGasPrice,
    // or for EIP-1559:
    maxFeePerGas: originalTx.maxFeePerGas * 1.2,
    maxPriorityFeePerGas: originalTx.maxPriorityFeePerGas * 1.2
  };

  return signer.sendTransaction(replacementTx);
}
