// Validium: off-chain DA with validity proofs
contract Validium {
    struct DataCommitment {
        bytes32 dataHash;
        address dataProvider;
    }

    function submitBatch(bytes32 stateRoot, DataCommitment calldata commitment, uint256[] calldata proof) external {
        require(verifyProof(proof, [stateRoot, commitment.dataHash]));
        commits[batchNum] = commitment;
    }
}
