// ve-Tokenomics: lock for voting power
function createLock(uint256 amount, uint256 duration) external {
    require(duration <= 4 years);
    uint256 votingPower = amount * duration / MAX_TIME;
    locks[msg.sender] = Lock({
        amount: amount,
        end: block.timestamp + duration,
        power: votingPower
    });
}
